public class multiplicationwithoutOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1=20;
		int num2=5;
		int sum=0;
		
        	for(int i=1;i<=num2;i++)
        	{
        		sum=sum+num1;   //i=1 =0+4..>4
        	}
        	
        	System.out.println("Multiplication is "+sum);
	}

}
